const withBookStoreService = () => (Wrapper) => {
    return props => (
        <BookStoreService.Consumer>
            {
                (bookStoreService) => (<Wrapper {...props} bookStoreService={bookStoreService} />)
            }
        </BookStoreService.Consumer>
    )
}


export default withBookStoreService
















