import React from 'react'
import BookList from '../book-list/index'
import withBooksStoreService from '../hoc/withBookStoreService'

const HomePage = (props) => {

    const books = props.bookStoreService.getBooks()

    return (
        <div>
            <BookList  books={books} />
        </div>
    )
}

export default withBooksStoreService()(HomePage)
